export { code as name } from '@/utils/constants/command.js';

export default function (editor) {
  editor.insert((selected) => {
    const prefix = '``` language';
    const suffix = '```';
    let text = `\n${prefix}\n${suffix}\n`;

    if (selected) {
      text = `\n${prefix}\n  ${selected}\n${suffix}\n`;
    }

    return {
      text,
      selected: 'language',
    };
  });
}
