<script lang="js">
import { reactive, defineComponent } from 'vue';

export default defineComponent({
  emits: ['onFormulaClick'],
  setup(props, context) {
    const formulaList = reactive([
      '\\frac{a}{b}', '\\sqrt[n]{ab}', '\\log_{a}{b}',
      '\\lim\\limits_{a \\rightarrow b}', 'c_a^b', '\\overleftarrow{ab}', '\\overrightarrow{ab}',
      // eslint-disable-next-line no-useless-escape
      '\\vec{x}', '\\sqrt{x}', '\\sum', '\\overline{ab}', '\\underline{ab}', '\\in', '\\ni', '\\int_{a}^{b}',
      '\\oint_a^b', '\\prod\\limits_a^b',
      '\\coprod\\limits_a^b', '\\bigcap\\limits_a^b', '\\bigcup\\limits_a^b', '\\bigsqcup\\limits_a^b', '\\sum\\limits_a^b',
      // eslint-disable-next-line no-useless-escape
      '\\lg{ab}', '\\times', '\\div', '\\propto', '\\geq', '\\leq', '\\neq',
    ]);
    function handleFormulaClick(item) {
      context.emit('onFormulaClick', item);
    }
    return {
      formulaList,
      handleFormulaClick
    }
  }
})

</script>
<template>
  <div class="formula-box">
    <vue-latex
      v-for="(item, index) in formulaList"
      display-mode
      class="formula"
      :key="index"
      :expression="item"
      @click="handleFormulaClick(item)"/>
  </div>
</template>
<style scoped>
.formula-box {
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  height: 150px;
}
.formula {
  display: flex;
  width: auto;
  padding: 0 18px;
  text-align: center;
  border: 1px solid #ABF;
  background: #EEF;
  cursor: pointer;
  vertical-align: top;
  height: 60px;
  align-items: center;
  margin: 6px 6px;
}
</style>
