import { insert } from '@/utils/constants/command';

export default {
  name: insert,
  icon: 'v-md-icon2-exit',
  title: '插入空格',
  action(editor) {
    editor.insert((selected) => {
      const content = selected? selected + '&nbsp;':'&nbsp;';

      return {
        text: content,
      };
    });
  },
};
