import { wrap } from '@/utils/constants/command';

export default {
  name: wrap,
  icon: 'v-md-icon2-md-return-left',
  title: '插入换行',
  action(editor) {
    editor.insert((selected) => {
      const content = selected? selected + '<br/>':'<br/>';
      return {
        text: content,
      };
    });
  },
};
